const addon=require('./build/Release/addon');
function jsSum(){
  let a=3.14,b=2.72;
  for(let i=0;i<100000000;i++){
    a+=b;
  }
  let total=a;
  return total;
}

exports.sum=function(){
	return addon.sum();
}
console.time('c++');
addon.sum();
console.timeEnd('c++');

console.time('js');
jsSum();
console.timeEnd('js');
